function habilitaJuros() {
    var op = document.getElementsByName("tpDivida")[0].value;
    if(op != "insJuros"){
        if(!document.getElementsByName("juros")[0].disabled) document.getElementsByName("juros")[0].disabled=true;
            switch(document.getElementsByName("tpDivida")[0].value){
                case "cartCred":
                document.getElementsByName("juros")[0].value = 14;
                break;

                case "emp":
                document.getElementsByName("juros")[0].value = 16;
                break;

                case "fin":
                document.getElementsByName("juros")[0].value = 2;
                break;
            }
    } else {
        if(document.getElementsByName("juros")[0].disabled) document.getElementsByName("juros")[0].disabled=false;
    }
}

function habilitaInvestimentos() {
    var op = document.getElementsByName("tpInvestimento")[0].value;

    if(op != "insTaxa"){
        if(!document.getElementsByName("taxa")[0].disabled) document.getElementsByName("taxa")[0].disabled=true;
            switch(document.getElementsByName("tpInvestimento")[0].value){
                case "poupanca":
                document.getElementsByName("taxa")[0].value = 4.55;
                break;

                case "tesouro":
                document.getElementsByName("taxa")[0].value = 5.28;
                break;

                case "cdbPre":
                document.getElementsByName("taxa")[0].value = 5.77;
                break;

                case "cdbPos":
                document.getElementsByName("taxa")[0].value = 5.83;
                break;

                case "lciPos":
                document.getElementsByName("taxa")[0].value = 6;
                break;

                case "lciPos2":
                document.getElementsByName("taxa")[0].value = 6.07;
                break;

                case "fundoFixo":
                document.getElementsByName("taxa")[0].value = 6.6;
                break;

                case "fundoMultiMerc":
                document.getElementsByName("taxa")[0].value = 11.24;
                break;
            }
    } else {
        if(document.getElementsByName("taxa")[0].disabled) document.getElementsByName("taxa")[0].disabled=false;
    }
}

function verificaSenha() {
    var senha1 = document.getElementsByName("pass")[0].value;
    var senha2 = document.getElementsByName("confirmPass")[0].value;

    if(senha1 != senha2) {
        document.getElementsByName("confirmPass")[0].setCustomValidity("Repita a senha corretamente");
    } else {
        document.getElementsByName("confirmPass")[0].setCustomValidity('');
    }
}